function removeInputClassErrors(){$("."+inputErrorClass).removeClass(inputErrorClass)}function removeErrorMessages(){$("."+messageAlertDiv).remove()}function errorProcess(a,b){removeInputClassErrors(),removeErrorMessages(),$(a).inputClassError(),$(a).errorMessageAfterElement(b),$(a).focus()}var inputErrorClass="has-error",messageAlertDiv="message-alert-div";Nette.addError=function(a,b){errorProcess(a,b)},$.fn.inputClassError=function(){$(this).addClass(inputErrorClass)},$.fn.errorMessageAfterElement=function(a){$(this).after('<div class="'+messageAlertDiv+'">'+a+"</div>")};